import { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

import styled from "styled-components";

const Styles = styled.div`
    {

    }
`;

function Detail({useParams}) {
    const [question, setQuestion] = useState({});

    useEffect(() => {
        async function getQuestion() {
          const response = await axios.get(`https://private-64010-blissrecruitmentapi.apiary-mock.com/questions/${useParams}`);
          setQuestion(response.data);
        }
        getQuestion();
      }, []);

    return (
        <Styles>
            <h2>Detail</h2>
            {question.id ? (
                <>
                    <h3>Question: {question.question}</h3>
                    <p>Id: {question.id}</p>
                    <p>Id: {question.id}</p>
                    <img src={question.image_url} alt={question.id} />
                    <img src={question.thumb_url} alt={question.id} />
                </>
            ) : (
                <p>Loading</p>
            ) }
        </Styles>
    );
};

export default Detail;
