import { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

import Search from "./Search";

import styled from "styled-components";

const Styles = styled.div`
    {
       background-color: green;
       height: 100vh; 
    }
`;

function Questions( ) {
    const [health, setHealth] = useState([]);
    const [questions, setQuestions] = useState([]);
    const [offset, setOffset] = useState(10)
    const [filter, setFilter] = useState("");

    useEffect(() => {
        async function getHealth() {
          const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
          setHealth(response.data);
        }
        getHealth();

        async function getAllQuestions() {
            console.log(filter)
            const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/questions?limit=10&offset=${offset}&filter=${filter}`);
            setQuestions(response.data);
          }
          getAllQuestions();
    }, []);

    // useEffect(() => {
    //     async function getAllQuestions() {
    //       const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/questions?limit=5&offset=5&filter=${filter}`);
    //       setQuestions(response.data);
    //     }
    //     getAllQuestions();
    // }, []);

    const handleRefresh = async () => {
        const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
        setHealth(response.data);
    }

    // const handleSearch = (search) => {
    //     setFilter(search)
    //     console.log(filter)
    // }

    const handleSearch = (e) => {
        const str = e.target.value;

        if (str) {
            setFilter(str)
            setSearchParams({ str });
        } else {
            setSearchParams({});
        }
    };

    return (
        <Styles>
            <h2>Questions</h2>
            {/* <Search setSearch={handleSearch} /> */}

            <form >
                <input type="text" value={search} onChange={handleSearch} placeholder="Search"/>
            </form>

            {health.status ? (
                <>
                    {health.status === "OK" ? (
                        <ul>
                            {questions.map((element) => {
                                return (
                                    <li key={element.id}>
                                        <NavLink to={`/questions/${element.id}`}>{element.question}</NavLink>
                                    </li>
                                );
                            })}
                        </ul>
                    ) : (
                        <button onClick={() => handleRefresh()}>Refresh</button>
                    )}
                </>
            ) : (
                <p>Loading</p>
            )}
        </Styles>
    );
};

export default Questions;
