`;

function Share({useParams}) {
    const [email, setEmail] = useState("")

    const handleEmailInput = e => setTitle(e.target.value);

    const handleForm = async (e) => {
        e.preventDefault();
        const email = "";
        await axios.post(
            `https://private-anon-cf3ea3a63b-blissrecruitmentapi.apiary-mock.com/share?destination_email=${email}&content_url=/share/${useParams}`
        );
    };

    return (
        <Styles>
            <form onSubmit={handleForm}>
                <label>Email: </label>
                <input 
                    type="text" 
                />
                <button type="submit">Share</button>
            </form>
        </Styles>
    );
};

export default Share;
