import { useState } from 'react';
import { useSearchParams } from "react-router-dom";


import styled from "styled-components";

const Styles = styled.div`
    {
       background-color: green;
       height: 100vh; 
    }
`;

function Search (props) {
    const [searchParams, setSearchParams] = useSearchParams();
    const [stringFilter, setStringFilter] = useState("");

    const search = searchParams.get("filter") || "";

    const handleSearchInput = (e) => {
        setStringFilter(e.target.value)

        if (stringFilter) {
            setSearchParams({ stringFilter });
        } else {
            setSearchParams({});
        }

        props.setFilter(stringFilter);
        console.log("filter: " + stringFilter)
    };

    return (
        <>
            <form >
                <input type="text" value={search} onChange={handleSearchInput} placeholder="Search"/>
                {/* <button onClick={() => props.setFilter("")}>Reset</button> */}
            </form>
        </>
    )
};

export default Search;
