import { useSearchParams } from "react-router-dom";


import styled from "styled-components";

const Styles = styled.div`
    {
       background-color: green;
       height: 100vh; 
    }
`;

function Search (props) {
    const [searchParams, setSearchParams] = useSearchParams();
    const search = searchParams.get("filter") || "";

    const [focused, setFocused] = useState(false)
    const onFocus = () => setFocused(true)

    useEffect(() => {
        async function getHealth() {
          const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
          setHealth(response.data);
        }
        getHealth();

        async function getAllQuestions() {
            const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/questions?limit=10&offset=${offset}&filter=${filter}`);
            setQuestions(response.data);
          }
          getAllQuestions();
    }, []);

    const handleChange = (e) => {
        const filter = e.target.value;

        if (filter) {
            props.setSearch(filter)
            setSearchParams({ filter });
        } else {
            setSearchParams({});
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // doesen't work
