import { useState, useEffect } from 'react';
import { useSearchParams, NavLink } from "react-router-dom";

import Share from "./Share";

import styled from "styled-components";

const Styles = styled.div`
    {
       background-color: green;
       height: 100vh; 
    }
`;

function Search (props) {
    const [searchParams, setSearchParams] = useSearchParams();


    let search = searchParams.get("filter") || "";


    const [focused, setFocused] = useState(false)
    const onFocusIn = () => {
        setFocused(true)
    }

    const onFocusOut = () => {
        setFocused(false)
        setSearchParams("")
    }

    const handleChange = (e) => {
        const filter = e.target.value;

        if (filter) {
            props.setSearch(filter)
            setSearchParams({ filter });
        } else {
            setSearchParams({});
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // doesen't work
        // search = "" // can't use an useState/setSearch !!!
        setSearchParams("")
        setFocused(false)
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <input type="text" value={search} onFocusIn={onFocusIn} onFocusOut={onFocusOut} onChange={handleChange} placeholder="Search"/>
                <button type="submit">Dismiss</button>
            </form>
            {focused && (<Share url={window.location.href} />)}
        </>
    )
};

export default Search;
