import { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

import Search from "./Search";

import Spinner from 'react-bootstrap/Spinner'

import styled from "styled-components";

const Main = styled.main`
    {
        min-height: 90vh;
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
        font-size: 1.05em;
    }

    .questions-card {
        min-width: 30vw;
        background-color: #AAD576;
        padding: 5vh;
        margin-top: 5vh;
    }

    h2 {
        font-size: 2.5em;
    }

    ul {
        list-style-type: none;
        padding-left: 0;

    }

    a{
        text-decoration: none;
        color: #1A4301;
    }

    a:hover{
        text-decoration: none;
        color: #AAD576;
    }

    .spinner-border {
    }

`;

function Questions( ) {
    const [health, setHealth] = useState([]);
    const [questions, setQuestions] = useState([]);
    const [limit, setLimit] = useState(10);
    const [offset, setOffset] = useState(10);
    const [filter, setFilter] = useState(null);

    useEffect(() => {
        async function getHealth() {
          const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
          setHealth(response.data);
        }
        getHealth();

        async function getAllQuestions() {
            const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/questions?limit=${limit}&offset=${offset}&filter=${filter}`);
            setQuestions(response.data);
          }
          getAllQuestions();
    }, []);

    const handleRefresh = async () => {
        const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
        setHealth(response.data);
    }

    const handleSearch = (search) => {
        setFilter(search)
        console.log("searchOnChange: " + search)
        console.log("filterOnChange: " + filter) // Delay of 1 change!!!
    }

    return (
        <Main>
            <div className="questions-card">
                <h2>Find Your Question</h2>
                <Search setSearch={handleSearch} />
                {health.status ? (
                    <>
                        {health.status === "OK" ? (
                            <ul>
                                {questions.map((element) => {
                                    return (
                                        <li key={element.id}>
                                            <NavLink to={`/questions/${element.id}`}>{element.question}</NavLink>
                                        </li>
                                    );
                                })}
                            </ul>
                        ) : (
                            <button onClick={() => handleRefresh()}>Refresh</button>
                        )}
                    </>
                ) : (
                        <Spinner animation="border" />
                )}
            </div>
        </Main>
    );
};

export default Questions;
