import { useState, useEffect } from "react";
import axios from "axios";

import Share from "./Share";

import Spinner from 'react-bootstrap/Spinner'

import styled from "styled-components";

const Main = styled.main`
    {
        min-height: 90vh;
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
        font-size: 1.05em;
        color: #538D22;
    }

    .green-card {
        min-width: 30vw;
        min-height: 60vh;
        border: 1px solid #AAD576;
        padding: 5vh;
        margin: 5vh 0;
        border-radius: 10px;
    }

    h2{
        font-family: "Roboto Slab";
        font-size: 2em;
        margin-bottom: 3.5vh;
    }

    form {
        display: flex;
        justify-content: center;
        align-items: center;
        margin: 2.5vh 0;

    }

    input {
        margin: 0 1em;
    }

    ul {
        list-style-type: none;
        padding-left: 0;
        display: flex;
        justify-content: space-around;
    }

    img {        
        max-width: 600px;
        width: 100%;
        height: auto;
    }

    #publishDate {
        color: #AAD576;
        margin: 0;
        text-align: right;
    }

    .spinner-border {
        margin-top: 20vh;
        color: #AAD576;
    }
`;

function Detail() {
    const [question, setQuestion] = useState({});

    useEffect(() => {
        async function getQuestion() {
          const response = await axios.get(`https://private-64010-blissrecruitmentapi.apiary-mock.com/questions/${question.id}`);
          setQuestion(response.data);
        }
        getQuestion();
      }, []);

    const handleVote = async () => {
        const response = await axios.put(
            `https://private-anon-cf3ea3a63b-blissrecruitmentapi.apiary-mock.com/questions/${question.id}`,
        );
        setQuestion(response.data);
    };

    return (
        <Main>
            <div className="green-card">
                {question.id ? (
                    <>
                        <h2>{question.question}</h2>
                        <Share url={window.location.href} />

                        <ul>
                            {question.choices.map((element, index) => {
                                return (
                                    <li key={index}>
                                        <h5>{element.choice}</h5> 
                                        <p>Votes: {element.votes}</p>
                                        <button onClick={() => handleVote(index)}>Vote</button>
                                    </li>
                                );
                            })}
                        </ul>

                        <img src={question.image_url} alt={question.id} />
                        <p id="publishDate">Published at: {question.published_at.slice(0,10)}</p>

                    </>
                ) : (
                    <Spinner animation="border" />
                ) }
            </div>
        </Main>
    );
};

export default Detail;
