import { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";


import styled from "styled-components";

const Styles = styled.div`
    {
       background-color: green;
       height: 100vh; 
    }
`;

function Questions( ) {
    const [health, setHealth] = useState([]);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        async function getHealth() {
          const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/health`);
          setHealth(response.data);
        }
        getHealth();
    }, []);

    useEffect(() => {
        async function getAllQuestions() {
          const response = await axios.get(`https://private-anon-e7b3f01cb5-blissrecruitmentapi.apiary-mock.com/questions?limit=5&offset=5&filter=choice`);
          setQuestions(response.data);
        }
        getAllQuestions();
    }, []);

    return (
        <Styles>
            <h2>Questions</h2>
            {health.status ? (
                <>

                </>
            ) : (
                <>

                </>
            )}

            {health.status === "OK" ? (
                <ul>
                    {questions.map((element) => {
                        return (
                            <li key={element.id}>
                                <NavLink to={`/questions/${element.id}`}>{element.question}</NavLink>
                            </li>
                        );
                    })}
                </ul>
            ) : (
                <button>Loading</button>
            )}

        </Styles>
    );
};

export default Questions;
